mapper.select:
      select real_name,f.phone,f.avatar,f.create_at,
        (select IFNULL(sum(amount/commission),0)
        from doctor_achievement_log where doctor_id=#{0}
        and source_type in('ORDER','REFUND')
				)as salesAmount,
        (select IFNULL(sum(amount/commission),0)
        from doctor_achievement_log where doctor_id=#{0}
        and source_type in('ORDER','REFUND')
        and create_at between CONCAT(date_add(curdate(),interval -day(curdate())+1 day),' 00:00:00')
        and  CONCAT(date_add(curdate()-day(curdate())+1,interval 1 month),' 00:00:00')
				)as amountOfMonth,
        (select IFNULL(sum(amount),0)
        from doctor_achievement_log where doctor_id=#{0}
        and source_type in('ORDER','SUB_DISTRIBUTOR','REFUND')
        and create_at between CONCAT(date_add(curdate(),interval -day(curdate())+1 day),' 00:00:00')
        and  CONCAT(date_add(curdate()-day(curdate())+1,interval 1 month),' 00:00:00')
				)as incomeAmountOfMonth
         from fruit_doctor f where id=#{0}

SELECT IFNULL(SUM(amount/commission),0) as salesAmount ,IFNULL(SUM(amount),0) AS incomeAmount,COUNT(DISTINCT order_id) AS orderCount
FROM doctor_achievement_log 

		source_type IN ('ORDER','REFUND')

		AND (DATE_FORMAT(create_at,'%Y-%m-%d') >= #{beginTime} AND DATE_FORMAT(create_at,'%Y-%m-%d') <= #{endTime})


		AND doctor_id = #{doctorId}


		AND user_id = #{userId}
